INSERT INTO cakes (description, price, weight, full_description) VALUES ('Chocolate Cake', 25, 400, 'A rich and moist chocolate cake made with premium cocoa, layered with velvety chocolate ganache and topped with dark chocolate shavings.');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Vanilla Cake', 20, 350, 'A classic vanilla cake with a light and fluffy texture, filled with creamy vanilla frosting and adorned with a hint of vanilla bean.');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Молочная девочка', 30, 750, 'Торт, который тает во рту. Вкуснейшие коржи из сгущённого молока и кремом "Пломбир"');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Орео торт', 24, 1000, 'Вкуснейший торт с хрустящими печеньками OREO. Влажные ванильные шифоновые бисквиты, шоколадный крем с кусочками печенья');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Красный бархат', 26, 800, 'Всеми любимый красный бархат. Важные красные коржики с вкуснейшим кремчизом');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Медовик', 35, 900, 'Торт, который напоминает детство. Ароматные воздушные медовые коржи со сливочно-сметанным кремом');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Морковный торт', 20, 650, 'Торт, который пахнет осенью. Пряные морковные коржи с кремчизом');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Банановый рай', 27, 800, 'Влажные шифоновые с бисквиты с прослойкой из свежих бананов , с нежнейшим банановым муссом, который просто тает во рту, и вкуснейшим кремчизом');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Летняя свежесть', 25, 500, 'Лёгкий летний тортик с приятным мятным послевкусием.  Шифоновые бисквиты , пропитанные малиновым сиропом, малиновый конфитюр дополняет простоту и красоту торта, а кремчиз с мятным сиропом делает его вкус особенным');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Сникерс', 23, 870, 'шоколадные шифоновые бисквиты с вкуснейшим кремчизом, с солёной карамелью и хрустящим ароматным арахисом');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Клубничный подарок', 19, 300, 'Торт, который пахнет летом. Шифоновые бисквиты  с прослойкой из свежей клубники и кремчизом');
INSERT INTO cakes (description, price, weight, full_description) VALUES ('Персик со сливками', 32, 1000, 'Обалденный персиковый тортик. Шифоновые бисквиты, пропитанные персиковым сиропом, прослойкой из натуральных персиков и кремчизом');
